SELECT u.Name BDM, COUNT(*) OpenProspects
FROM Salesforce..Lead l
inner join Salesforce..[User] u ON l.OwnerId collate latin1_general_CS_AS = u.Id collate latin1_general_CS_AS
WHERE Status = 'open' and (Suspended_Closed_Reason__c = '' or Suspended_Closed_Reason__c is null) and l.Affinity_Cold__c = 'Cold' and
l.SIC2007_Code__c not in ('A','B','D','E','K','R')
		and l.SIC2007_Code3__c not in ('85200','75000','86102','87900','88990','84120','86220','30990','29202','29100','52230','49100','52103','49410','49390','52219','77120','49319','50100','77342','51210','52290','49420','77110','52243','53202','23610','23690','28923','23630','31010','16230','28230','23510','41202','42910','1629','42990','43110','43290','23310','14120','28220','43220','46730','43999','41100','16290','16100','17120','23190','16210','46130','23120','23130','43342','23110','43910','9100','35110','71111','71200','43130','71122','33200','28302','28132','28950','24410','28110','28240','28990','71121','71129','25920','22220','82920','96030','21100','46460','47730','20412','81291','81222','81299','81210','96010','80100','84240','80200','46220','1130','20150','1190','20200','71112','46610','46310','47760','81300','24420','25610','32130','23700','25710','20301','25500','32120','13990','20160','22210')
		and l.SIC2007_Code3__c not in ('23140','26701','27110','27320','29320','23320','25990','28150','24100','26110','46690','22190','24200','25730','28250','32990','27900','25110','25930','49200','49311','49320','50200','50300','50400','51101','51102','51220','52211','52220','56101','69101','69109','69202','69203','74901','77341','77351','77352','77390','79110','79120','82200','82911','82912','86101','86210','94110','94120','94200','94910','94920','94990','99999','69102','86230','64110','64191','64192','64306','47110','47190','47220','47230','47240','47290','47300','47421','47510','47520','47530','47540','47591','47599','47610','47620','47640','47650','47710','47722','47782','47789','47910','47990','55100','55300','55900','56103','56210','56302','93110','93120','93199','93290')
		and
		LEFT(l.PostalCode,4) not in ('EX31','EX33','EX34','EX35','EX39')
		and LEFT(l.PostalCode,4) not between 'PA20' and 'PA80'
		and LEFT(l.PostalCode,4) not between 'EX20' and 'EX23'
		and LEFT(l.PostalCode,4) not between 'NP1' and 'NP20'
		and l.Area_Code__c not in ('KW','IV','BT','LN','SA','LD','HR','TR','PL','TQ','DT','AB')
		and
		l.FT_Employees__c between 6 and 225
		and
		(l.Phone <> '' or l.Phone is not null)
GROUP BY u.Name