SELECT a.ID
INTO #openOpps
FROM Salesforce..Account a
inner join Salesforce..Opportunity o ON a.Id = o.AccountId
WHERE o.StageName not like '%Closed%'
GROUP BY a.Id

SELECT *
INTO #FirstContact
FROM
(
SELECT AccountId, FirstName, LastName, MobilePhone, OtherPhone, Email, ROW_NUMBER() OVER (PARTITION BY AccountId ORDER BY CreatedDate) rn
FROM Salesforce..Contact
) detail
WHERE rn = 1

SELECT o.Id, a.Name Company, a.BillingStreet, a.BillingCity, a.BillingState, a.BillingPostalCode PostalCode, a.Phone, a.FT_Employees__c, 
fc.FirstName, fc.LastName, fc.MobilePhone, fc.OtherPhone, fc.Email,
o.Lost_Reason_Notes__c, 'Winback' LeadSource, '012D0000000Ncc7IAC' RecordTypeId
INTO SalesforceReporting..Winback_August
FROM Salesforce..Opportunity o
inner join Salesforce..Account a ON o.AccountId = a.Id
left outer join #openOpps oo ON a.Id = oo.Id
left outer join #FirstContact fc ON a.Id = fc.AccountId
