IF OBJECT_ID('tempdb..#LastLogin') IS NOT NULL
	BEGIN
		DROP TABLE #LastLogin
	END

IF OBJECT_ID('tempdb..#EmpCount') IS NOT NULL
	BEGIN
		DROP TABLE #EmpCount
	END
	
IF OBJECT_ID('tempdb..#userCount') IS NOT NULL
	BEGIN
		DROP TABLE #userCount
	END
	
CREATE TABLE #LastLogin 
	(
    CompanyId nvarchar(255),
    LastLoginDate datetime
	)

INSERT INTO #LastLogin 
SELECT CompanyId, CAST(MAX(DateAdd(Month, -1, t.Expiry)) as datetime) 
FROM [identity].Tokens t
inner join Shared.Users u ON t.subjectId = u.email
inner join Citation.Companies c ON u.companyId = c.Id
GROUP BY CompanyId

SELECT CompanyId, COUNT(Id) emps
INTO #EmpCount
FROM dbo.Employees
WHERE IsLeaver = 0
GROUP BY companyId

SELECT CompanyId, COUNT(Id) users
INTO #UserCount
FROM Shared.Users
WHERE IsActive = 1
GROUP BY companyId

