ISNULL(CONVERT(VarChar,ll.LastLoginDate),'') LastLogin, 
CONVERT(VarChar,ISNULL(ec.Emps,0)) Employees, 
CONVERT(VarChar,ISNULL(uc.Users,0)) Users
FROM Citation.Companies c
left outer join #LastLogin ll ON c.Id = ll.CompanyId
left outer join #EmpCount ec ON c.Id = ec.CompanyId
